  { id: 'atlantis', title: 'Atlantis', year: '2023', image: atlantis, tags: [figmaIcon, reactIcon, tailwindIcon, postgreIcon], figmaLink: 'https://www.figma.com/design/PMR3EiQAf5er2mwd8s8v0F/ITNC?m=auto&t=i7UMDbc3K6X779rm-6' },
  { id: 'periodicos', title: 'Portal de Periódicos', year: '2023', image: periodicos, tags: [figmaIcon], figmaLink: 'https://www.figma.com/design/rfU5C1QPJwQjoSptRTVm01/Prot%C3%B3tipos-site-de-peri%C3%B3dicos?m=auto&t=i7UMDbc3K6X779rm-6' },
]);


const projects = computed(() => {
  return projectsBase.value.map(project => ({
    ...project,
    description: texts.value.project_details[project.id]?.description || project.description,
  }));
});

const timelineItems = computed(() => {
  return timelineItemsBase.value.map(baseItem => {
    const translatedContent = texts.value.timeline[baseItem.type][baseItem.id];
    return {
      ...baseItem,
      ...translatedContent,
    };
  }).sort((a, b) => parseInt(b.period) - parseInt(a.period));
});

const workExperience = computed(() =>
  timelineItems.value.filter(item => item.type === 'work')
);

const educationHistory = computed(() =>
  timelineItems.value.filter(item => item.type === 'education')
);

onMounted(() => {
  const observer = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.classList.add('is-visible');
      }
    });
  }, {
    threshold: 0.1
  });

  const elementsToAnimate = document.querySelectorAll('.animate-on-scroll');
  elementsToAnimate.forEach(el => observer.observe(el));
});

const formStatus = ref({
  submitting: false,
  success: false,
  error: '',
});

async function handleSubmit(event) {
  formStatus.value = { submitting: true, success: false, error: '' };
  const formData = new FormData(event.target);
  const data = Object.fromEntries(formData.entries());

  try {
    const response = await fetch('/api/send-email', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });

    if (!response.ok) {
      let errorMessage = `Erro no servidor: ${response.status}`;
      try {
        const errorResult = await response.json();
        errorMessage = errorResult.message || errorMessage;
      } catch (e) {
      }
      throw new Error(errorMessage);
    }

    const responseBody = await response.text();
    if (!responseBody) {
        console.log("Sucesso, mas sem corpo de resposta.");
        formStatus.value.success = true;
        event.target.reset();
        return; // Termina a função aqui
    }

    const result = JSON.parse(responseBody);
    formStatus.value.success = true;
    event.target.reset(); // Limpa o formulário

  } catch (error) {
    formStatus.value.error = error.message;
  } finally {
    formStatus.value.submitting = false;
  }
}
