  <section class="parallax bg2 bg-cover pt-20">
    <section id="projetos" class="w-9/12 mx-auto lg:mb-20">
      <h2 class="text-center text-4xl font-semibold text-white pb-12 animate-on-scroll">{{ texts.projects.title }}</h2>
      <div class="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div v-for="(project, index) in projects" :key="project.title"
          class="bg-white rounded-lg flex flex-col overflow-hidden shadow-2xl transition-all duration-300 hover:-translate-y-2 animate-on-scroll"
          :style="{ 'transition-delay': `${index * 100}ms` }">
          <img :src="project.image" :alt="'Screenshot do projeto ' + project.title" class="w-full h-56 object-cover" />
          <div class="p-6 flex flex-col flex-grow">
            <span class="flex items-baseline gap-3 mb-2">
              <h3 class="font-bold text-2xl text-gray-800">{{ project.title }}</h3>
              <p class="text-gray-500">({{ project.year }})</p>
            </span>
            <p class="text-gray-600 mb-4 flex-grow">{{ project.description }}</p>
            <div class="flex flex-col gap-4 lg:gap-0 lg:flex-row justify-between items-center mt-auto">
              <ul class="flex  items-center gap-2">
                <li v-for="(tag, i) in project.tags" :key="i">
                  <img :src="tag" class="w-8 h-8 object-contain" />
                </li>
              </ul>
              <div class="flex items-center gap-4">
                <a v-if="project.repoLink" :href="project.repoLink" target="_blank"
                  class="font-semibold text-gray-600 hover:text-[#002668]">{{ texts.projects.view_code }}</a>
                <a v-if="project.liveLink" :href="'https://' + project.liveLink" target="_blank"
                  class="px-5 py-2 bg-[#002668] text-white rounded-lg font-semibold hover:bg-[#0A1D3E]">{{
                    texts.projects.view_online }}</a>
                <a v-if="project.figmaLink" :href="project.figmaLink" target="_blank"
                  class="px-5 py-2 bg-[#002668] text-white rounded-lg font-semibold hover:bg-[#0A1D3E]">{{
                    texts.projects.view_figma }}</a>
              </div>
            </div>
          </div>
        </div>
      </div>
